	public function assignByHash($result) {
		$this->setEmailId($result['emailid']);
		$this->setFromEmail($result['from_email']);
		$this->setToEmail($result['to_email']);
		$this->setCcEmail($result['cc_email']);
		$this->setBCcEmail($result['bcc_email']);
		$this->setAssignedUserEmail($result['assigned_user_email']);
		$this->setIdLists($result['idlists']);
		$this->setEmailFlag($result['email_flag']);
	}

	/**
	 * Get element instance by it's primary key(s).
	 * Will return null if no row was matched.
	 *
	 * @param PDO $db
	 * @return VTigerEmailDetails
	 */
	public static function findById(PDO $db,$emailId) {
		$stmt=self::prepareStatement($db,self::SQL_SELECT_PK);
		$stmt->bindValue(1,$emailId);
		$affected=$stmt->execute();
		if (false===$affected) {
			$stmt->closeCursor();
			throw new Exception($stmt->errorCode() . ':' . var_export($stmt->errorInfo(), true), 0);
		}
		$result=$stmt->fetch(PDO::FETCH_ASSOC);
		$stmt->closeCursor();
		if(!$result) {
			return null;
		}
		$o=new VTigerEmailDetails();
		$o->assignByHash($result);
		$o->notifyPristine();
		return $o;
	}

	/**
	 * Bind all values to statement
