		$stmt->bindValue(2,$this->getFromEmail());
		$stmt->bindValue(3,$this->getToEmail());
		$stmt->bindValue(4,$this->getCcEmail());
		$stmt->bindValue(5,$this->getBCcEmail());
		$stmt->bindValue(6,$this->getAssignedUserEmail());
		$stmt->bindValue(7,$this->getIdLists());
		$stmt->bindValue(8,$this->getEmailFlag());
	}


