		$this->bindValues($stmt);
		$stmt->bindValue(8,$this->getEventHandlerId());
		$stmt->bindValue(9,$this->getEventName());
		$stmt->bindValue(10,$this->getHandlerClass());
		$affected=$stmt->execute();
		if (false===$affected) {
			$stmt->closeCursor();
			throw new Exception($stmt->errorCode() . ':' . var_export($stmt->errorInfo(), true), 0);
		}
		$stmt->closeCursor();
		$this->notifyPristine();
		return $affected;
	}


