		$this->setPresence($result['presence']);
		$this->setDefaultValue($result['defaultvalue']);
		$this->setMaximumLength($result['maximumlength']);
		$this->setSequence($result['sequence']);
		$this->setBlock($result['block']);
		$this->setDisplayType($result['displaytype']);
		$this->setTypeOfData($result['typeofdata']);
		$this->setQuickCreate($result['quickcreate']);
		$this->setQuickCreateSequence($result['quickcreatesequence']);
		$this->setInfoType($result['info_type']);
		$this->setMassEditable($result['masseditable']);
		$this->setHelpInfo($result['helpinfo']);
	}

	/**
	 * Get element instance by it's primary key(s).
	 * Will return null if no row was matched.
	 *
	 * @param PDO $db
	 * @return VTigerField
	 */
	public static function findById(PDO $db,$fieldId) {
		$stmt=self::prepareStatement($db,self::SQL_SELECT_PK);
		$stmt->bindValue(1,$fieldId);
		$affected=$stmt->execute();
		if (false===$affected) {
			$stmt->closeCursor();
			throw new Exception($stmt->errorCode() . ':' . var_export($stmt->errorInfo(), true), 0);
		}
		$result=$stmt->fetch(PDO::FETCH_ASSOC);
		$stmt->closeCursor();
		if(!$result) {
			return null;
		}
		$o=new VTigerField();
		$o->assignByHash($result);
		$o->notifyPristine();
		return $o;
	}

	/**
	 * Bind all values to statement
	 *
	 * @param PDOStatement $stmt
	 */
	protected function bindValues(PDOStatement &$stmt) {
		$stmt->bindValue(1,$this->getTabId());
		$stmt->bindValue(2,$this->getFieldId());
		$stmt->bindValue(3,$this->getColumnName());
		$stmt->bindValue(4,$this->getTableName());
		$stmt->bindValue(5,$this->getGeneratedType());
		$stmt->bindValue(6,$this->getUiType());
		$stmt->bindValue(7,$this->getFieldName());
		$stmt->bindValue(8,$this->getFieldLabel());
		$stmt->bindValue(9,$this->getReadOnly());
		$stmt->bindValue(10,$this->getPresence());
		$stmt->bindValue(11,$this->getDefaultValue());
		$stmt->bindValue(12,$this->getMaximumLength());
		$stmt->bindValue(13,$this->getSequence());
		$stmt->bindValue(14,$this->getBlock());
		$stmt->bindValue(15,$this->getDisplayType());
		$stmt->bindValue(16,$this->getTypeOfData());
		$stmt->bindValue(17,$this->getQuickCreate());
		$stmt->bindValue(18,$this->getQuickCreateSequence());
		$stmt->bindValue(19,$this->getInfoType());
		$stmt->bindValue(20,$this->getMassEditable());
		$stmt->bindValue(21,$this->getHelpInfo());
	}


	/**
	 * Insert this instance into the database
	 *
	 * @param PDO $db
	 * @return mixed
	 */
	public function insertIntoDatabase(PDO $db) {
		if (null===$this->getFieldId()) {
			$stmt=self::prepareStatement($db,self::SQL_INSERT_AUTOINCREMENT);
			$stmt->bindValue(1,$this->getTabId());
			$stmt->bindValue(2,$this->getColumnName());
			$stmt->bindValue(3,$this->getTableName());
			$stmt->bindValue(4,$this->getGeneratedType());
			$stmt->bindValue(5,$this->getUiType());
			$stmt->bindValue(6,$this->getFieldName());
			$stmt->bindValue(7,$this->getFieldLabel());
			$stmt->bindValue(8,$this->getReadOnly());
			$stmt->bindValue(9,$this->getPresence());
			$stmt->bindValue(10,$this->getDefaultValue());
			$stmt->bindValue(11,$this->getMaximumLength());
			$stmt->bindValue(12,$this->getSequence());
			$stmt->bindValue(13,$this->getBlock());
			$stmt->bindValue(14,$this->getDisplayType());
			$stmt->bindValue(15,$this->getTypeOfData());
			$stmt->bindValue(16,$this->getQuickCreate());
			$stmt->bindValue(17,$this->getQuickCreateSequence());
			$stmt->bindValue(18,$this->getInfoType());
			$stmt->bindValue(19,$this->getMassEditable());
			$stmt->bindValue(20,$this->getHelpInfo());
		} else {
			$stmt=self::prepareStatement($db,self::SQL_INSERT);
			$this->bindValues($stmt);
		}
		$affected=$stmt->execute();
		if (false===$affected) {
			$stmt->closeCursor();
			throw new Exception($stmt->errorCode() . ':' . var_export($stmt->errorInfo(), true), 0);
		}
		$lastInsertId=$db->lastInsertId();
		if (false!==$lastInsertId) {
			$this->setFieldId($lastInsertId);
		}
		$stmt->closeCursor();
		$this->notifyPristine();
		return $affected;
	}


	/**
	 * Update this instance into the database
	 *
	 * @param PDO $db
	 * @return mixed
	 */
	public function updateToDatabase(PDO $db) {
		$stmt=self::prepareStatement($db,self::SQL_UPDATE);
		$this->bindValues($stmt);
		$stmt->bindValue(22,$this->getFieldId());
		$affected=$stmt->execute();
		if (false===$affected) {
			$stmt->closeCursor();
			throw new Exception($stmt->errorCode() . ':' . var_export($stmt->errorInfo(), true), 0);
		}
		$stmt->closeCursor();
		$this->notifyPristine();
		return $affected;
	}


	/**
	 * Delete this instance from the database
	 *
	 * @param PDO $db
	 * @return mixed
	 */
	public function deleteFromDatabase(PDO $db) {
		$stmt=self::prepareStatement($db,self::SQL_DELETE_PK);
		$stmt->bindValue(1,$this->getFieldId());
		$affected=$stmt->execute();
		if (false===$affected) {
			$stmt->closeCursor();
			throw new Exception($stmt->errorCode() . ':' . var_export($stmt->errorInfo(), true), 0);
		}
		$stmt->closeCursor();
		return $affected;
	}

