	 *
	 * @param DOMDocument $doc
	 * @return VTigerProject[]
	 */
	public static function fromDOMDocument(DOMDocument $doc) {
		$instances=array();
		foreach ($doc->getElementsByTagName('VTigerProject') as $node) {
