	 * `vtiger_purchaseorder`.`vendorid` -> `vtiger_vendor`.`vendorid`
	 *
	 * @param PDO $db a PDO Database instance
	 * @param array $sort array of DSC instances
	 * @return VTigerVendor
	 */
	public function fetchVTigerVendor(PDO $db, $sort=null) {
		$filter=array(VTigerVendor::FIELD_VENDORID=>$this->getVendorId());
		$result=VTigerVendor::findByFilter($db, $filter, true, $sort);
		return empty($result) ? null : $result[0];
	}

