	public static function fromDOMElement(DOMElement $node) {
		$o=new VTigerPurchaseOrder();
		$o->assignByHash(self::domNodeToHash($node, self::$FIELD_NAMES, self::$DEFAULT_VALUES, self::$FIELD_TYPES));
